    53	// 记账类
    54	class Account implements AccountFeature {
    55	    private ArrayList<Record> records;
    56	    private double balance;
    57	
    58	    // 初始化账本
    59	    public void init() {
    60	        records = new ArrayList<>();
    61	        balance = 0;
    62	    }
    63	
    64	    // 显示主菜单
    65	    public void showMainMenu() {
    66	        System.out.println("1. 显示账单信息");
    67	        System.out.println("2. 登记收入信息");
    68	        System.out.println("3. 登记支出信息");
    69	        System.out.println("4. 退出记账器");
    70	        System.out.print("请选择(1-4)：");
    71	    }
    72	
    73	    // 显示账单信息
    74	    public void showDetailInfo() {
    75	        System.out.println("----- 账单信息 -----");
    76	        if (records.isEmpty()) {
    77	            System.out.println("暂无账单记录");
    78	        } else {
    79	            for (Record record : records) {
    80	                System.out.println("类型：" + record.getRecordType());
    81	                System.out.println("金额：" + record.getAmountOfMoney());
    82	                System.out.println("余额：" + record.getCurBalance());
    83	                System.out.println("备注：" + record.getRemark());
    84	                System.out.println("时间：" + record.getDate());
    85	                System.out.println("--------------------");
    86	            }
    87	        }
    88	    }
    89	
    90	    // 登记收入信息
    91	    public void editIncomeInfo() {
    92	        Scanner scanner = new Scanner(System.in);
    93	        System.out.println("请输入收入金额：");
    94	        double amount = scanner.nextDouble();
    95	        System.out.println("请输入备注：");
    96	        String remark = scanner.next();
    97	        // 获取当前时间
    98	        Date date = new Date();
    99	        // 更新余额
   100	        double curBalance = balance + amount;
   101	        // 添加记录
   102	        Record record = new Record("收入", curBalance, amount, remark, date);
   103	        records.add(record);
   104	        balance = curBalance;
   105	        System.out.println("收入信息已登记");
   106	    }
   107	
   108	    // 登记支出信息
   109	    public void editSpendInfo() {
   110	        Scanner scanner = new Scanner(System.in);
   111	        System.out.println("请输入支出金额：");
   112	        double amount = scanner.nextDouble();
   113	        // 检查余额是否足够支出
   114	        if (amount > balance) {
   115	            System.out.println("余额不足，无法完成支出登记");
   116	            return;
   117	        }
   118	        System.out.println("请输入备注：");
   119	        String remark = scanner.next();
   120	        // 获取当前时间
   121	        Date date = new Date();
   122	        // 更新余额
   123	        double curBalance = balance - amount;
   124	        // 添加记录
   125	        Record record = new Record("支出", curBalance, amount, remark, date);
   126	        records.add(record);
   127	        balance = curBalance;
   128	        System.out.println("支出信息已登记");
   129	    }
   130	
   131	    // 退出记账器
   132	    public void exitAccount() {
   133	        System.out.println("退出记账器，谢谢使用！");
   134	        System.exit(0); // 退出程序
   135	    }
   136	}
   137	
   138	// 自定义异常类
   139	class CustomException extends Exception {
   140	    private String errorMessage;
   141	
   142	    public CustomException(String errorMessage) {
   143	        this.errorMessage = errorMessage;
   144	    }
   145	
   146	    public String getErrorMessage() {
   147	        return errorMessage;
   148	    }
   149	}
   150	
   151	// 主类
   152	public class accounter {
   153	    public static void main(String[] args) {
   154	        Account account = new Account();
   155	        account.init();
   156	        Scanner scanner = new Scanner(System.in);
   157	
   158	        while (true) {
   159	            try {
   160	                account.showMainMenu();
   161	                int choice = scanner.nextInt();
   162	                switch (choice) {
   163	                    case 1:
   164	                        account.showDetailInfo();
   165	                        break;
   166	                    case 2:
   167	                        account.editIncomeInfo();
   168	                        break;
   169	                    case 3:
   170	                        account.editSpendInfo();
   171	                        break;
   172	                    case 4:
   173	                        account.exitAccount();
   174	                        return;
   175	                    default:
   176	                        throw new CustomException("参数错误，请重新输入");
   177	                }
   178	            } catch (CustomException e) {
   179	                System.out.println(e.getErrorMessage());
   180	                scanner.nextLine(); // 清除输入缓冲区
   181	            }
   182	        }
   183	    }
   184	}...
